<?phpclass MAX_BOL{	/**	 * This is a unique identifier of plugin.	 */	protected $plugin_name;	protected $version;	/**	 * The core functionality of the plugin.	 *	 */	public function __construct() {		if ( defined( 'MAX_BOL_VERSION' ) ) {			$this->version = MAX_BOL_VERSION;		} else {			$this->version = '1.0';		}		$this->plugin_name = 'max-bol';		$this->load_dependencies();        $this->define_admin_hooks();	}	/**	 * Load the required dependencies.	 *	 * Include the required files:	 *	 */	private function load_dependencies() {        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'includes/settings/fields-settings.php';        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'admin/class-max-bol-admin.php';        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'includes/class-max-bol-settings.php';        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'includes/class-max-bol-loader.php';        $this->loader = new MAX_BOL_Loader();	}	/**	 * Register admin hooks	 * of the plugin.	 *	 */	private function define_admin_hooks() {		$plugin_admin = new MAX_BOL_Admin( $this->get_plugin_name(), $this->get_version() );        $this->loader->add_action( 'admin_enqueue_scripts', $plugin_admin, 'enqueue_styles' );        $this->loader->add_action( 'admin_enqueue_scripts', $plugin_admin, 'enqueue_scripts' );        $this->loader->add_filter( 'manage_edit-shop_order_columns',$plugin_admin, 'max_add_column_order_table_function' , 50, 50);        $this->loader->add_filter( 'manage_shop_order_posts_custom_column',$plugin_admin, 'max_add_column_data_order_table_function' );        $this->loader->add_action( 'wp_ajax_nopriv_max_import_orders_from_api', $plugin_admin, 'max_import_orders_from_api' );        $this->loader->add_action( 'wp_ajax_max_import_orders_from_api', $plugin_admin, 'max_import_orders_from_api' );        $this->loader->add_filter( 'cron_schedules', $plugin_admin,  'max_bol_shedule_cron_job_account_1' );        $this->loader->add_action( 'init', $plugin_admin, 'max_get_bol_orders_list_cron_job_account_1' );        $this->loader->add_action( 'max_bol_shedule_cron_job_account_1_as', $plugin_admin, 'max_get_bol_orders_list_cron_job_callback_account_1' );        //$this->loader->add_filter( 'cron_schedules', $plugin_admin,  'max_bol_shedule_cron_job_account_2' );//        $this->loader->add_action( 'admin_head', $plugin_admin, 'max_get_bol_orders_list_cron_job_account_2' );        $this->loader->add_action( 'max_bol_shedule_cron_job_account_2_as', $plugin_admin, 'max_get_bol_orders_list_cron_job_callback_account_2' );//        $this->loader->add_action( 'admin_head', $plugin_admin, 'max_get_bol_orders_list_cron_job_callback_account_1' );//        $this->loader->add_action( 'admin_head', $plugin_admin, 'max_get_bol_orders_list_cron_job_callback_account_2' );	}	/**     * Run the loader.     *     */    public function max_run() {        $this->loader->max_run();    }	/**	 * The name of the plugin	 *	 */	public function get_plugin_name() {		return $this->plugin_name;	}	/**	 * The version number of the plugin.	 */	public function get_version() {		return $this->version;	}}